DROP TABLE IF EXISTS "crontasks";
CREATE TABLE "crontasks" ("id" INTEGER,"taskid" INTEGER,"status" INTEGER,"param" TEXT,"sname" TEXT,"desc" TEXT,"dt" TEXT,"sdt" TEXT,"login" TEXT, "cron" TEXT, "worker_threads" INTEGER);
DROP TABLE IF EXISTS "def_val";
CREATE TABLE "def_val" ("id" INTEGER, "sname" TEXT, "val" TEXT);
DROP TABLE IF EXISTS "sequ";
CREATE TABLE sequ (id int);
DROP TABLE IF EXISTS "session";
CREATE TABLE `session` (id integer,   login TEXT,   secret TEXT,   dt TEXT );
DROP TABLE IF EXISTS "snmpworker";
CREATE TABLE "snmpworker" ("id" INTEGER PRIMARY KEY ,"sname" TEXT,"desc" TEXT,"worker" text,"cgiscript" text,"table_header" TEXT DEFAULT (null) ,"export_param" TEXT, "worker_body" TEXT);
DROP TABLE IF EXISTS "tasks";
CREATE TABLE "tasks" ("id" INTEGER,"sname" TEXT,"desc" TEXT,"login" TEXT DEFAULT (null) ,"pdt" text,"sdt" text,"dt" text,"param" text,"status" INTEGER,"outfile" text,"progress" INTEGER,"mess" text,"worker_threads" INTEGER, "crontaskid" INTEGER);
DROP TABLE IF EXISTS "users";
CREATE TABLE `users` (  id INTEGER PRIMARY KEY AUTOINCREMENT ,     login TEXT,    name TEXT,   password TEXT );
